import React, { Component } from 'react'

class ArtistSignUp extends Component {
    constructor(props){
        super(props);
        this.state = {
            name: '',
            email: '',
            pass: '',
            confirmpass: '',
            instagram
        };

        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.resetFields = this.resetFields.bind(this);
    }

    onChange(event) {
        this.setState({ [event.target.name]: event.target.value});
    }

    onSubmit(event) {
        event.preventDefault();
        if(!(this.state.email).includes("@") || !(this.state.email).includes(".")){
            alert("Please enter a valid email address.");
            this.state.email = '';
        }
        else if(this.state.pass == ""){
            alert("Password field cannot be left blank.")
        }
        else if(this.state.pass != this.state.confirmpass){
            alert("Passwords do not match.")
            this.state.pass = '';
            this.state.confirmpass = '';
        }
        else{
            const post = {
                name: this.state.name,
                email: this.state.email,
                pass:  this.state.pass,
                confirmpass: this.state.confirmpass,
                instagram: this.state.instagram
            }

            fetch('https://jsonplaceholder.typicode.com/posts', {
                method: 'POST',
                headers: {
                    'content-type': 'application/json'
                },
                pass: JSON.stringify(post),
                name: JSON.stringify(post),
                email: JSON.stringify(post),
                instagram: JSON.stringify(post)
            }).then( resp =>resp.json())
            .then(data => console.log(data));

            this.resetFields();
        }

    }

    resetFields(){
        this.state.name = '';
        this.state.email = '';
        this.state.pass = '';
        this.state.confirmpass = '';
        this.state.instagram = '';
    }

  render() {
    return (
      <div>
        <h1>Artist Sign Up</h1>
        <form onSubmit={this.onSubmit}>
            <div>
                <label>Name:</label><br/>
                <input placeHolder = "*Name" type="text" name="name" onChange={this.onChange} value={this.state.name}/>
            </div>
            <div>
                <label>Email:</label><br/>
                <input placeHolder = "*Email" type="text" name="email" onChange={this.onChange} value={this.state.email}/>
            </div>
            <br/>
            <div>
                <label>Password:</label><br/>
                <input placeHolder = "*Password" type="text" name="pass" onChange={this.onChange} value={this.state.pass}/>
            </div>
            <div>
                <label>Confirm Password:</label><br/>
                <input placeHolder = "*Confirm Password" type="text" name="confirmpass" onChange={this.onChange} value={this.state.confirmpass}/>
            </div>
            <div>
                <label>Instagram:</label><br/>
                <input placeHolder = "*Instagram (Don't have an Instagram, enter N/A" type="text" name="instagram" onChange={this.onChange} value={this.state.instagram}/>
            </div>
            <br/>
            <button type="submit">Sign Up</button>
        </form>
      </div>
    )
  }
}

export default ArtistSignUp;
